mapper.select:
SELECT SQL_CALC_FOUND_ROWS student.id, student.name, elective.id as elective, course.name as course, teacher.name as teacher, clazz.name as clazz, specialized.name as specialized, faculty.name as faculty, elective.score as score, clazz.clazzYear as clazzYear, course.courseYear as courseYear, course.term
FROM t_teacher teacher, t_course course, t_elective elective, t_student student, t_clazz clazz, t_specialized specialized, t_faculty faculty
student.name like '%' #{name} '%'
and student.id = #{id}
and teacher.id = #{teacher}
and faculty.id = #{faculty}
and specialized.id = #{specialized}
and clazz.id = #{clazz}
and course.id = #{course}
and course.courseYear = #{courseYear}
and course.term = #{term}
and clazz.clazzYear = #{clazzYear}
and course.name like '%' #{courseName} '%'
AND course.teacher = teacher.id AND elective.course = course.id AND elective.student = student.id AND student.clazz = clazz.id AND clazz.specialized = specialized.id AND specialized.faculty = faculty.id
order by student.id asc
LIMIT #{skipCount},#{limit}

SELECT SQL_CALC_FOUND_ROWS room.id, room.number, room.name, room.capacity, room.roomType, building.id building, building.number buildingNumber, building.name buildingName, building.buildingType FROM t_room room, t_building building
room.building=building.id
and room.id = #{id}
and room.number = #{number}
and room.name like '%' #{name} '%'
and room.building = #{building}
and room.roomType = #{roomType}
order by room.number asc
LIMIT #{skipCount},#{limit}
